CREATE TABLE IF NOT EXISTS orders (id INT AUTO_INCREMENT PRIMARY KEY, `name` VARCHAR(50) NOT NULL, created_at DATETIME(6) DEFAULT CURRENT_TIMESTAMP(6) NOT NULL);
CREATE TABLE IF NOT EXISTS order_items (id INT AUTO_INCREMENT PRIMARY KEY, menu_id INT NOT NULL, order_id INT NOT NULL, created_at DATETIME(6) DEFAULT CURRENT_TIMESTAMP(6) NOT NULL, CONSTRAINT fk_order_items_menu_id__id FOREIGN KEY (menu_id) REFERENCES menus(id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT fk_order_items_order_id__id FOREIGN KEY (order_id) REFERENCES orders(id) ON DELETE CASCADE ON UPDATE CASCADE);
