CREATE TABLE IF NOT EXISTS menus (id INT AUTO_INCREMENT PRIMARY KEY, `name` VARCHAR(50) NOT NULL, price FLOAT NOT NULL, category INT NOT NULL, image VARCHAR(50) NOT NULL, created_at DATETIME NULL);
CREATE TABLE IF NOT EXISTS orders (id INT AUTO_INCREMENT PRIMARY KEY, `name` VARCHAR(50) NOT NULL, status INT NOT NULL, additional_notes text NULL, table_no INT NOT NULL, created_at DATETIME NULL);
CREATE TABLE IF NOT EXISTS order_items (id INT AUTO_INCREMENT PRIMARY KEY, menu_id INT NOT NULL, order_id INT NOT NULL, created_at DATETIME NULL, CONSTRAINT fk_order_items_menu_id__id FOREIGN KEY (menu_id) REFERENCES menus(id) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT fk_order_items_order_id__id FOREIGN KEY (order_id) REFERENCES orders(id) ON DELETE CASCADE ON UPDATE CASCADE);
